sys_variable_value.value:
(function execute(inputs, outputs) {
	var utils = new AzureAutomationUtils();
    utils.validateInputVariables(inputs, outputs);
    utils.validateMandatoryVariables(inputs, ['subscription_id', 'resource_group_name','account_name','update_name']);
    utils.mapToOutputFromInput(inputs, outputs, ['subscription_id', 'resource_group_name','account_name','update_name']);
  	utils.createUpdate(inputs, outputs);
})(inputs, outputs);

