sys_variable_value.value:
(function execute(inputs, outputs) {
    var successCodes = ['200', '202', '201'];
    var response = JSON.parse(inputs.response_body);
    var arrayUtil = new global.ArrayUtil();
    if (arrayUtil.contains(successCodes, inputs.status)) {
        outputs.status = 'Success';
    } else {
        outputs.status = 'Error';
        if (response.message)
            outputs.error_message = response.message;
        else
            outputs.error_message = response.error.message;
    }
})(inputs, outputs);

